import org.apache.commons.io.FilenameUtils;

//Texts array contains pairs consisting of a text fed to speller and correction expected in responce
def texts = [
        ["карова", "корова"],
        ["малако", "молоко"],
        ["облоко", "облако"],
        ["перескоп", "перископ"],
        ["пречина", "причина"],
        ["колобог", "колобок"],
        ["машына", "машина"],
        ["побдорка", "подборка"],
        ["працэнт", "процент"],
        ["абажюр", "абажур"]
]

//1. Get counter value
Integer counter = Integer.parseInt(testRunner.testCase.getPropertyValue("counter"))

//2. If counter is greater than zero, perform test
if (counter > 0) {
    //3. Update test case properties to reflect current test step corresponding to the counter
    def arrayIndex = texts.size() - counter
    testRunner.testCase.setPropertyValue("text", texts[arrayIndex][0])
    testRunner.testCase.setPropertyValue("expeсtedCorrection", texts[arrayIndex][1])
    def result = testRunner.testCase.testSteps["CheckText"].run(testRunner, context)

    //4. Log test result to consle
    log.info "text: " + texts[arrayIndex][0] + ", result: " + result.getStatus().toString()

   //5. Log test result to file
    try {
    	   def resultsFolderPath = testRunner.testCase.getPropertyValue("resultsFolderPath")
    	   def resultsFileName =  testRunner.testCase.getPropertyValue("resultsFileName")
    	   def resultsFile = new File(resultsFolderPath, resultsFileName)
        def testSuite = testRunner.testCase.testSuite.name
        def testCase = testRunner.testCase.name
        def testStep = result.getTestStep()
        def testStepName = testStep.name
        def type = testStep.config.type
        def status = result.getStatus()
        Date d = new Date();
        def executionDate = d.format("dd-MM-yyyy_HH_mm")
        resultsFile.append('\n')
        resultsFile.append('"' + testSuite + '",')
        resultsFile.append('"' + testCase + '",')
        resultsFile.append('"' + testStepName + '",')
        resultsFile.append('"' + type + '",')
        resultsFile.append('"' + status + '",')
        resultsFile.append('"' + executionDate + '",')
        resultsFile.append('"text: ' + texts[arrayIndex][0] + ', expected correction: ' + texts[arrayIndex][1] + '"')
    }
    catch (e) {
        log.error("Exception happened: " + e.toString())
    }

    //6. Update counter
    counter--
    testRunner.testCase.setPropertyValue("counter", counter.toString())

    //7. Recursive call
    testRunner.testCase.testSteps["CheckValidTextsContainingErrors"].run(testRunner, context)
}

