import org.apache.commons.io.FilenameUtils;

def texts = [
        ["карова", "корова"],
        ["малако", "молоко"],
        ["облоко", "облако"],
        ["перескоп", "перископ"],
        ["пречина", "причина1"],
        ["колобог", "колобок"],
        ["машына", "машина"],
        ["побдорка", "подборка"],
        ["працэнт", "процент"],
        ["абажюр", "абажур"]
]

Integer counter = Integer.parseInt(testRunner.testCase.getPropertyValue("counter"))

if (counter > 0) {
    counter--
    testRunner.testCase.setPropertyValue("text", texts[counter][0])
    testRunner.testCase.setPropertyValue("expeсtedCorrection", texts[counter][1])
    def result = testRunner.testCase.testSteps["CheckText"].run(testRunner, context)

    log.info(result.getStatus().toString())

    try {
    	   def resultsFolderPath = testRunner.testCase.getPropertyValue("resultsFolderPath")
    	   def resultsFileName =  testRunner.testCase.getPropertyValue("resultsFileName")
    	   def resultsFile = new File(resultsFolderPath, resultsFileName)
        def testSuite = testRunner.testCase.testSuite.name
        def testCase = testRunner.testCase.name
        def testStep = result.getTestStep()
        def testStepName = testStep.name
        def type = testStep.config.type
        def status = result.getStatus()
         Date d = new Date();
         def executionDate = d.format("dd-MM-yyyy_HH_mm");
        resultsFile.append('\n')
        resultsFile.append('"' + testSuite + '",')
        resultsFile.append('"' + testCase + '",')
        resultsFile.append('"' + testStepName + '",')
        resultsFile.append('"' + type + '",')
        resultsFile.append('"' + status + '",')
        resultsFile.append('"' + executionDate + '",')
        resultsFile.append('"Text: ' + texts[counter][0] + ', expected correction: ' + texts[counter][1] + '"')
    }
    catch (e) {
        log.error("Exception happened: " + e.toString());
    }

    testRunner.testCase.setPropertyValue("counter", counter.toString())
    testRunner.testCase.testSteps["CheckValidTextsContainingErrors"].run(testRunner, context)
}

import org.apache.commons.io.FilenameUtils;

try {
    //1. Create project results folder
    def projectPath = new com.eviware.soapui.support.GroovyUtils(context).projectPath
    def projectFileNameWithoutExtension = FilenameUtils.removeExtension(new File(testRunner.getTestCase().getTestSuite().getProject().getPath()).getName())
    def resultsFolderPath = projectPath + "/" + projectFileNameWithoutExtension + "_soapui-results"
    new File(resultsFolderPath).mkdirs()

    //2. Create results file
    Date d = new Date();
    def executionDate = d.format("dd-MM-yyyy_HH_mm");
    def resultsFileName = "report_" + executionDate + ".csv"
    def resultsFile = new File(resultsFolderPath, resultsFileName)
    if (!resultsFile.exists()) {
        resultsFile.createNewFile()
        resultsFile.write('"Test Suite","Test Case","Test Step","Step Type","Step Status","Execution Date","Additional Info"')
    }

    //3. Add file info to proect properties
    testRunner.testCase.setPropertyValue("resultsFolderPath", resultsFolderPath)
    testRunner.testCase.setPropertyValue("resultsFileName", resultsFileName)
}
catch (e) {
    log.error("Exception happened: " + e.toString());
}
